import React, { useRef } from 'react'
import './signin.css'
import { useDispatch } from 'react-redux';
import { setUser } from '../../Slices/user.slice';


const Signin = () => {

  const emailRef = useRef()
  const passwordRef = useRef()

  const dispatch = useDispatch()

  const handleSubmit = async () => {
    const email = emailRef.current.value
    const password = passwordRef.current.value

    const response = await fetch('/api/auth/signin', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ email, password }),
    })
    
    if (!response.ok) {
      const error = await response.json()
      alert(error.error)
    }
    else{
      const data = await response.json()
      localStorage.setItem('user', JSON.stringify(data))
      dispatch(setUser(data))
      window.location.href = '/main' 
    }
  }

  return (
    <div className="login-container">
      <div className="left">
        <h1 >Integrate Slido for a <br /> seamless experience</h1>
        <p >
          Integrations make it easy to present, manage and <br /> join Slido wherever you meet.
          Try them now and say <br /> goodbye to switching.
        </p>
        <img className='icons-image' src="data:image/png;base64," alt="" />
        <video className='video-login'
             autoPlay 
              loop 
              playsInline 
              muted
              src="https://auth.slido.com/static/81abf2e14578ba14.mp4"></video>
      </div>
      <div className="right">
        <h2 style={{fontSize:"40px"}}>Log in <br /> to your account</h2>
        <p style={{color:"gray"}}>Haven't signed up yet? <a href="/signup">Create account</a></p>
        <button className="login-btn webex">Log in with Webex</button>
        <button className="login-btn google">Log in with Google</button>
        <p style={{color:'gray'}}>or</p>
        <div className="email-login">
          <input type="email" placeholder="Email" ref={emailRef} />
          <input type="password" placeholder="Password" ref={passwordRef} />
          <button className="continue-btn" onClick={handleSubmit}>Continue</button>
        </div>
      </div>
    </div>
  )
}

export default Signin